import { useState, useEffect } from 'react';

function StartupsList() {

    const { SearchClient, AzureKeyCredential } = require("@azure/search-documents");

    const endpoint = process.env.REACT_APP_search_endpoint;
    const indexName = process.env.REACT_APP_index_name;
    const searchKey = process.env.REACT_APP_search_api_key;

    const client = new SearchClient(endpoint, indexName, new AzureKeyCredential(searchKey));

    const [startups, setStartups] = useState([]);
    
    useEffect(() => {
        getStartups()
    }, []);

    const getStartups = async() => {
        const searchResults = await client.search("Pre-A");
        let startupList = [];
        for await (const result of searchResults.results) {
            startupList.push(result.document)            
        }
        setStartups(startupList);
    }
    

     return (
        <div className="StartupsList px-5">
            <table className="table">
            <thead>
                <tr>
                    <th scope="col">Name</th>
                    <th scope="col">Startup Name</th>
                    <th scope="col">Location</th>
                    <th scope="col">Industry</th>
                    <th scope="col">Startup Stage</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <th scope="row">1</th>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td>@mdo</td>
                </tr>
                {startups.map((startup) => (
                    <tr key={startup.StartupID}>
                        <td>{startup.Name}</td>
                        <td>{startup.StartupName}</td>
                        <td>{startup.Location}</td>
                        <td>{startup.Industry}</td>
                        <td>{startup.StartupStage}</td>
                    </tr>
                ))}

            </tbody>
            </table>
        </div>
    );
  }
  
  export default StartupsList;
